import React from "react";
import Button from "@material-ui/core/Button";
import CssBaseline from "@material-ui/core/CssBaseline";
import TextField from "@material-ui/core/TextField";
import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import AuthenticationService from "./AuthenticationService";

class SignIn extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
    };

    this.bindHandlers();
  }

  componentDidMount() {
    //this.loadData();
  }

  async loadData() {
    await fetch("http://localhost:8801/design/users/getAllUsers", {
      method: "GET",
      Credentials: "include",
      headers: {
        Authorization: AuthenticationService.createBasicAuthToken(
          "jesus",
          "12345"
        ),
      },
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("data = " + JSON.stringify(result));
          this.setState({
            users: result,
          });
        },
        (error) => {
          // do nothing
        }
      );
  }

  render() {
    const classes = makeStyles((theme) => ({
      paper: {
        marginTop: theme.spacing(8),
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      },
      avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
      },
      form: {
        width: "100%", // Fix IE 11 issue.
        marginTop: theme.spacing(1),
      },
      submit: {
        margin: theme.spacing(3, 0, 2),
      },
    }));

    return (
      <Container component="main" maxWidth="xs">
        <CssBaseline />
        <div className={classes.paper}>
          <Typography component="h1" variant="h5">
            Welcome to the Home Design Page
          </Typography>
          <form className={classes.form} noValidate>
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              id="email"
              label="Email Address"
              name="email"
              autoComplete="email"
              autoFocus
              onChange={this.usernameTextInputHandler}
            />
            <TextField
              variant="outlined"
              margin="normal"
              required
              fullWidth
              name="password"
              label="Password"
              type="password"
              id="password"
              autoComplete="current-password"
              onChange={this.passwordTextInputHandler}
            />
            <Button
              type="submit"
              fullWidth
              variant="contained"
              color="primary"
              className={classes.submit}
              onClick={this.handleSubmitButton}
            >
              Sign In
            </Button>
          </form>
        </div>
      </Container>
    );
  }

  async handleSubmitButton() {
    console.log("Clicked!");
    console.log("username = ", this.state.username);
    console.log("password = ", this.state.password);

    const response = await AuthenticationService.executeBasicAuthenticationService(
      this.state.username,
      this.state.password
    );

    console.log("RESPONSE EXECUTE AUTH = " + JSON.stringify(response));

    //   .then(() => {
    //     console.log("JESUS LOGGED IN");
    //     AuthenticationService.registerSuccessfulLogin(
    //       this.state.username,
    //       this.state.password
    //     );
    //   })
    //   .catch(() => {
    //     console.log("JESUS ERROR LOGGED IN");
    //   });
  }

  usernameTextInputHandler(event) {
    this.setState({ username: event.target.value });
  }

  passwordTextInputHandler(event) {
    this.setState({ password: event.target.value });
  }

  bindHandlers() {
    this.handleSubmitButton = this.handleSubmitButton.bind(this);
    this.usernameTextInputHandler = this.usernameTextInputHandler.bind(this);
    this.passwordTextInputHandler = this.passwordTextInputHandler.bind(this);
  }
}

export default SignIn;
