import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import AuthenticationService from "./AuthenticationService";
import {
  CircularProgress,
  Fade,
  Snackbar,
  Button,
  Grid,
  Modal,
} from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import NewUser from "./NewUser";

class UsersTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      loading: true,
      openSnackbar: false,
    };
  }

  componentDidMount() {
    this.loadData();
  }

  async loadData() {
    await fetch("http://localhost:8801/design/users/getAllUsers", {
      headers: {
        Authorization: AuthenticationService.createBasicAuthToken(
          "jesus",
          "12345"
        ),
      },
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("data = " + result);
          this.setState({
            users: result,
            loading: false,
            openSnackbar: false,
          });
        },
        (error) => {
          this.setState({
            users: [],
            loading: false,
            openSnackbar: true,
          });
        }
      );
  }
  // const res = await fetch("http://localhost:8801/design/users/getAllUsers");
  //   console.log("res = ", res);
  //   try {
  //     const result = await res.json();
  //     console.log("result = ", result);
  //     this.setState({
  //       rows: result,
  //     });
  //   } catch (Exception) {
  //     console.log("result = ", Exception);
  //   }

  insertUser() {
    fetch("http://localhost:8801/design/users/createUser", {
      method: "POST",
      headers: {
        Authorization: AuthenticationService.createBasicAuthToken(
          "jesus",
          "12345"
        ),
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        username: "new2@mail.com",
        firstName: "new2",
        lastName: "ls2",
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("data = " + result);
          this.loadData();
        },
        (error) => {
          // Do nothing
        }
      );
  }

  render() {
    const classes = makeStyles({
      table: {
        minWidth: 650,
      },
    });

    const handleCloseSnackbar = (event, reason) => {
      if (reason === "clickaway") {
        return;
      }

      this.setState({ openSnackbar: false });
    };

    const addUserClickHandler = (event) => {
      //this.insertUser();
      this.setState({ showNewUserModal: true });
    };

    return (
      <TableContainer component={Paper}>
        <Fade in={this.state.loading}>
          <CircularProgress />
        </Fade>
        <Snackbar
          open={this.state.openSnackbar}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
        >
          <Alert onClose={handleCloseSnackbar} severity="error">
            Something went wrong in the backend!
          </Alert>
        </Snackbar>
        <Grid
          container
          direction="row"
          justify="flex-start"
          alignItems="flex-start"
          spacing={3}
        >
          <Grid item xs={1}>
            <Button
              variant="contained"
              color="primary"
              onClick={addUserClickHandler}
            >
              Add User
            </Button>
          </Grid>
        </Grid>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Id</TableCell>
              <TableCell>Username</TableCell>
              <TableCell>First Name</TableCell>
              <TableCell>Last Name</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.users.map((user) => (
              <TableRow key={user.id}>
                <TableCell component="th" scope="row">
                  {user.id}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.username}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.firstName}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.lastName}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="simple-modal-title"
          aria-describedby="simple-modal-description"
        >
          {NewUser}
        </Modal>
      </TableContainer>
    );
  }
}

export default UsersTable;

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}
