import React from "react";
import "./App.css";
import UsersTable from "./UsersTable";

// function createData(id, username, firstName, lastName) {
//   return { id, username, firstName, lastName };
// }

// const rows = [
//   createData("0", "us1", "name1", "last1"),
//   createData("1", "us2", "name2", "last2"),
//   createData("2", "us3", "name3", "last3"),
//   createData("3", "us4", "name4", "last4"),
//   createData("4", "us5", "name5", "last5"),
// ];

let rows = [];

class App extends React.Component {
  // constructor(props) {
  //   super(props);
  //   this.state = {
  //     rows: rows,
  //   };
  // }

  constructor(props) {
    super(props);
    this.state = {
      rows: [],
    };
    this.loadData();
  }

  loadData() {
    fetch("http://localhost:8801/design/users/getAllUsers", {
      method: "GET",
      mode: "no-cors",
      cache: "no-cache",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("FERMIN data = " + result);
          this.setState({
            rows: result.data,
          });
        },
        (error) => {
          // do nothing
        }
      );
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <p>Jesus Lorca Luque</p>
          <UsersTable rows={this.rows} />
        </header>
      </div>
    );
  }
}

export default App;
