import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import AuthenticationService from "./AuthenticationService";
import {
  CircularProgress,
  Fade,
  Snackbar,
  Button,
  Grid,
} from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";

class UsersTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      loading: true,
      openSnackbar: false,
    };
  }

  componentDidMount() {
    this.loadData();
  }

  async loadData() {
    await fetch("http://localhost:8801/design/users/getAllUsers", {
      headers: {
        Authorization: AuthenticationService.createBasicAuthToken(
          "jesus",
          "12345"
        ),
      },
    })
      .then((res) => res.json())
      .then(
        (result) => {
          console.log("data = " + result);
          this.setState({
            users: result,
            loading: false,
            openSnackbar: false,
          });
        },
        (error) => {
          this.setState({
            users: [],
            loading: false,
            openSnackbar: true,
          });
        }
      );
  }
  // const res = await fetch("http://localhost:8801/design/users/getAllUsers");
  //   console.log("res = ", res);
  //   try {
  //     const result = await res.json();
  //     console.log("result = ", result);
  //     this.setState({
  //       rows: result,
  //     });
  //   } catch (Exception) {
  //     console.log("result = ", Exception);
  //   }

  render() {
    const classes = makeStyles({
      table: {
        minWidth: 650,
      },
    });

    const handleCloseSnackbar = (event, reason) => {
      if (reason === "clickaway") {
        return;
      }

      this.setState({ openSnackbar: false });
    };

    return (
      <TableContainer component={Paper}>
        <Fade in={this.state.loading}>
          <CircularProgress />
        </Fade>
        <Snackbar
          open={this.state.openSnackbar}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
        >
          <Alert onClose={handleCloseSnackbar} severity="error">
            Something went wrong in the backend!
          </Alert>
        </Snackbar>
        <Grid
          container
          direction="row"
          justify="flex-end"
          alignItems="flex-start"
        >
          <Grid item spacing={3} xs={12}>
            <Button variant="contained" color="primary">
              Primary
            </Button>
          </Grid>
        </Grid>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>Id</TableCell>
              <TableCell>Username</TableCell>
              <TableCell>First Name</TableCell>
              <TableCell>Last Name</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {this.state.users.map((user) => (
              <TableRow key={user.id}>
                <TableCell component="th" scope="row">
                  {user.id}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.username}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.firstName}
                </TableCell>
                <TableCell component="th" scope="row">
                  {user.lastName}
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  }
}

export default UsersTable;

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}
